/* generator.js */

/**
 * An encapsulation of Chance.js that servers as an iteration-generator.
 * Only generate data when data is needed.
 */

var chance = require('chance').Chance();
var format = require('util').format;
var debug = require('debug')('generator');

chance.mixin({
  'user': function() {
    return {
      first: chance.first(),
      last: chance.last(),
      email: chance.email()
    };
  }
});

/**
 * DataStream Constructor
 * @param schema - a Schema object (generated by schema.js)
 * @param dataLength - the number of documents to generate
 */
var DataStream = function (schema, dataLength) {
  if (!(this instanceof DataStream)) {
    return new DataStream(schema, dataLength);
  }
  this.dataLength = dataLength;
  this.restLength = dataLength;

  // add schema-specific template to Chance.js
  chance.mixin({
    _: function () {
      // todo: more complex logic
      var o = {};
      var field, type;
      for (field in schema) {
        type = schema[field];
        o[field] = chance[type]();
      }
      return o;
    }
  });
  // log
  debug('OP: DataStream successfully built');
  debug('VERBOSE:' + this.toString());
};

/**
 * @return number of documents not yet generated
 */
DataStream.prototype.numLeft = function () {
  return this.restLength;
};

/**
 * @return boolean of whether there is more data to generate
 */
 DataStream.prototype.hasMore = function () {
  return this.restLength > 0;
};

/**
 * Similar to next() method of an iterator
 *
 * @param step - max number of data to generate per this request
 * @return an array of documents conforming to the schema
 * @throws TypeError, StopIteration
 */
DataStream.prototype.emit = function (step) {
  if (step === undefined) step = 1;
  if (typeof step !== 'number') {
    throw new Error('Not a number');
  }
  if (!this.hasMore()) {
    throw new Error('no more data to emit');
  }

  var i, data = [];
  step = Math.min(step, this.restLength);
  this.restLength -= step;
  for (i = 0; i < step; i++) {
    data.push(chance._());
  }

  debug('OP: DataStream emitted %d docs, %d left', step, this.restLength);

  return data.slice(0);
};

DataStream.prototype.testEmit = function () {
  return chance._();
};

DataStream.prototype.toString = function () {
  return format('<DataStream:%d/%d,%j>',
    this.restLength, this.dataLength, this.testEmit());
};

module.exports = DataStream;
